mapper.update:
update board
set BCOUNT=BCOUNT+1
where bNo = #{bNo}

update board
set btype=#{bType}
where bNo = #{bNo}

update board
set btype=#{bType},BTITLE=#{bTitle},MODIFYDATE = sysdate
where bNo=#{bNo}

update board
set bstatus='N'
where bNo = #{bNo}

mapper.select:
select count(*)
from board
join EMPLOYEE using(empno)
where BSTATUS='Y'
and (
BCONTENT LIKE #{searchKey}
or BTITLE LIKE #{searchKey}
or EMPNO LIKE #{searchKey}
or EMPNAME LIKE #{searchKey}
)

select *
from board
join EMPLOYEE using(empno)
where BSTATUS='Y'
and (
BCONTENT LIKE #{searchKey}
or BTITLE LIKE #{searchKey}
or EMPNO LIKE #{searchKey}
or EMPNAME LIKE #{searchKey}
)

order by BCOUNT DESC


order by BDATE DESC


order by BDATE ASC


order by BNO ASC
