mapper.select:
select deptname
from DEPARTMENT
where deptno = #{deptNo}

mapper.update:
update EMPLOYEE
set password =#{password}
where empNo = #{empNo}

mapper.select:
select count(*)
from EMPLOYEE
JOIN DEPARTMENT USING (DEPTNO)
where EMPSTATUS != 'N'
AND DEPTNO != 'MMMMM'
AND
(
DEPTNO LIKE #{search}
OR DEPTNAME LIKE #{search}		
OR EMPNAME LIKE #{search}
OR PHONE LIKE #{search}
OR EMPPHONE LIKE #{search}
OR EMPNO LIKE #{search}
OR EMAIL LIKE #{search}
)

